useEffect(() => {
  if (messages.length === 0) return;

  const message = dequeue();

  switch (message?.type) {
    case 'ORDER_CREATE':
      if (isOwnAppOrder(message.type, message.content.order)) {
        const order = message.content.order;
        ack(...);
        nativeFunction.showOrderPopup(...);
        addOrder(order);
      }
      break;

    case 'ORDER_UPDATE':
      if (isOrderMessage(message.type, message.content)) {
        const key = LocalStorage.getLocalStorageItem(AuthEntity.Const.MULTI_LOGIN_KEY);

        if (window.nativeApp && (key === message.multiLoginKey || message.multiLoginKey === null)) {
          if (shouldPrintCancelReceipt) window.nativeApp.printPrintCancelReceipt(order);
          if (shouldPrintAcceptReceipt) window.nativeApp.printPrintOnAccept(order);
        }
      }
      break;
  }
}, [messages]);

